	/**
	 * Social Icons – domain mappings.
	 *
	 * By default, each Icon ID is matched against a .com TLD. To override this behavior,
	 * specify all the domains it covers (including the .com TLD too, if applicable).
	 *
	 * @var array
	 */
	protected static $social_icons_map = array(
		'behance' => array(
			'behance.net',
		),
		'codepen' => array(
			'codepen.io',
		),
		'facebook' => array(
			'facebook.com',
			'fb.me',
		),
		'feed' => array(
			'feed',
		),
		'mail' => array(
			'mailto:',
		),
		'twitch' => array(
			'twitch.tv',
		),
	);

	/**
	 * Gets the SVG code for a given icon.
	 *
	 * @static
	 *
	 * @param string $group The icon group.
	 * @param string $icon  The icon.
	 * @param int    $size  The icon-size in pixels.
	 * @return string
	 */
	public static function get_svg( $group, $icon, $size ) {

		if ( 'ui' === $group ) {
			$arr = self::$icons;
		} elseif ( 'social' === $group ) {
			$arr = self::$social_icons;
		} else {
			$arr = array();
		}

		/**
		 * Filters Starter's array of icons.
		 *
		 * The dynamic portion of the hook name, `$group`, refers to
		 * the name of the group of icons, either "ui" or "social".
		 *
		 * @param array $arr Array of icons.
		 */
		$arr = apply_filters( "strt_svg_icons_{$group}", $arr );

		$svg = '';
		if ( array_key_exists( $icon, $arr ) ) {
			$repl = sprintf( '<svg class="svg-icon bi" width="%d" height="%d" aria-hidden="true" role="img" focusable="false" ', $size, $size );

			$svg = preg_replace( '/^<svg /', $repl, trim( $arr[ $icon ] ) ); // Add extra attributes to SVG code.
		}

		// @phpstan-ignore-next-line.
		return $svg;
	}

	/**
	 * Detects the social network from a URL and returns the SVG code for its icon.
	 *
	 * @static
	 *
	 * @param string $uri  Social link.
	 * @param int    $size The icon-size in pixels.
	 * @return string|null
	 */
	public static function get_social_link_svg( $uri, $size ) {
		static $regex_map; // Only compute regex map once, for performance.

		if ( ! isset( $regex_map ) ) {
			$regex_map = array();

			/**
			 * Filters Starter's array of domain mappings for social icons.
			 *
			 * By default, each Icon ID is matched against a .com TLD. To override this behavior,
			 * specify all the domains it covers (including the .com TLD too, if applicable).
			 *
			 * @param array $social_icons_map Array of default social icons.
			 */
			$map = apply_filters( 'strt_social_icons_map', self::$social_icons_map );

			/**
			 * Filters Starters's array of social icons.
			 *
			 * @param array $social_icons Array of default social icons.
			 */
			$social_icons = apply_filters( 'strt_svg_icons_social', self::$social_icons );

			foreach ( array_keys( $social_icons ) as $icon ) {
				$domains            = array_key_exists( $icon, $map ) ? $map[ $icon ] : array( sprintf( '%s.com', $icon ) );
				$domains            = array_map( 'trim', $domains ); // Remove leading/trailing spaces, to prevent regex from failing to match.
				$domains            = array_map( 'preg_quote', $domains );
				$regex_map[ $icon ] = sprintf( '/(%s)/i', implode( '|', $domains ) );
			}
		}
		foreach ( $regex_map as $icon => $regex ) {
			if ( preg_match( $regex, $uri ) ) {

				return self::get_svg( 'social', $icon, $size );
			}
		}
		return null;
	}
}
