/**
 * WooCommerce Email - Add text before table when BACS is used
 */
add_action( 'woocommerce_email_before_order_table', 'strt_add_content_specific_email', 20, 4 );
function strt_add_content_specific_email($order, $sent_to_admin, $plain_text, $email) {
	if ( ! class_exists( 'WC_Payment_Gateways' ) ) return;
	if ( 'customer_completed_order' === $email->id && 'bacs' === $order->payment_method ) {
		$gateways           = WC_Payment_Gateways::instance(); // gateway instance.
		$available_gateways = $gateways->get_available_payment_gateways();
		if ( isset( $available_gateways['bacs'] ) ) {
			echo '<p>U hebt ervoor gekozen om op factuur te betalen. U kunt de betaling o.v.v. het factuurnummer overmaken aan:</p>
			<p><strong>Stichting Passionate Bulkboek</strong><br />
			Rekeningnummer: NL14INGB0005620336<br />
			BIC: INGBNL2A<br />
			BTW NR.: 8045.3766.5 B01<br />
			KvK: 41135304</p>';
		}
	}
}

