/**
 * @snippet Add CSS to WooCommerce Emails
 */
function strt_add_css_to_emails( $css, $email ) {
	$css .= '
	address.address {
		border: none;
		padding: 0;
		font-style: normal;
	}
	.socket-logos {
		margin-top: 20px;
		display: flex;
		flex-flow: row wrap; 
		justify-content: center;
		align-items: center;
	}
	.socket-logo {
		margin: 0 10px 5px;
	}
	.svg-icon {
		height: auto;
	}
	';
	return $css;
}
add_filter( 'woocommerce_email_styles', 'strt_add_css_to_emails', 9999, 2 );

